import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Quiz = ({ quizData, user, onLogout }) => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);
  const [timeLeft, setTimeLeft] = useState(300); // 5 minutes per quiz
  const [quizStarted, setQuizStarted] = useState(false);
  const navigate = useNavigate();

  const currentQuestion = quizData.questions[currentQuestionIndex];
  const totalQuestions = quizData.questions.length;
  const progress = ((currentQuestionIndex + 1) / totalQuestions) * 100;

  useEffect(() => {
    let timer;
    if (quizStarted && timeLeft > 0 && !showResults) {
      timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
    } else if (timeLeft === 0 && !showResults) {
      handleSubmitQuiz();
    }
    return () => clearTimeout(timer);
  }, [timeLeft, quizStarted, showResults]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const handleAnswerSelect = (answerIndex) => {
    setSelectedAnswers({
      ...selectedAnswers,
      [currentQuestionIndex]: answerIndex
    });
  };

  const handleNextQuestion = () => {
    if (currentQuestionIndex < totalQuestions - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      handleSubmitQuiz();
    }
  };

  const handlePreviousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  const handleSubmitQuiz = () => {
    setShowResults(true);
  };

  const calculateScore = () => {
    let correctAnswers = 0;
    quizData.questions.forEach((question, index) => {
      if (selectedAnswers[index] === question.correct) {
        correctAnswers++;
      }
    });
    return correctAnswers;
  };

  const getScorePercentage = () => {
    return Math.round((calculateScore() / totalQuestions) * 100);
  };

  const getScoreMessage = () => {
    const percentage = getScorePercentage();
    if (percentage >= 90) return "Excellent! Outstanding performance!";
    if (percentage >= 80) return "Great job! Well done!";
    if (percentage >= 70) return "Good work! Keep it up!";
    if (percentage >= 60) return "Not bad! Room for improvement.";
    return "Keep studying! You can do better!";
  };

  const startQuiz = () => {
    setQuizStarted(true);
  };

  const restartQuiz = () => {
    navigate('/topic');
  };

  if (!quizStarted) {
    return (
      <div className="container">
        <div className="card">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
            <h1 className="title" style={{ margin: 0, fontSize: '2rem' }}>Quiz Ready!</h1>
            <button onClick={onLogout} className="btn btn-secondary">
              Logout
            </button>
          </div>

          <div style={{ textAlign: 'center', marginBottom: '30px' }}>
            <h2 style={{ color: '#333', marginBottom: '10px' }}>Topic: {quizData.topic}</h2>
            <p style={{ color: '#666', fontSize: '1.1rem' }}>
              {totalQuestions} Questions â€¢ {quizData.difficulty.charAt(0).toUpperCase() + quizData.difficulty.slice(1)} Difficulty
            </p>
            <p style={{ color: '#999', marginTop: '10px' }}>
              Time Limit: 5 minutes
            </p>
          </div>

          <div style={{ background: '#f8f9fa', padding: '20px', borderRadius: '10px', marginBottom: '30px' }}>
            <h3 style={{ marginBottom: '15px', color: '#333' }}>Quiz Instructions:</h3>
            <ul style={{ textAlign: 'left', color: '#666', lineHeight: '1.6' }}>
              <li>Read each question carefully</li>
              <li>Select the best answer from the 4 options</li>
              <li>You can navigate between questions using Previous/Next buttons</li>
              <li>You have 5 minutes to complete the quiz</li>
              <li>Your score will be calculated based on correct answers</li>
            </ul>
          </div>

          <button onClick={startQuiz} className="btn" style={{ fontSize: '1.2rem', padding: '15px 30px' }}>
            Start Quiz
          </button>
        </div>
      </div>
    );
  }

  if (showResults) {
    const score = calculateScore();
    const percentage = getScorePercentage();

    return (
      <div className="container">
        <div className="card">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
            <h1 className="title" style={{ margin: 0, fontSize: '2rem' }}>Quiz Complete!</h1>
            <button onClick={onLogout} className="btn btn-secondary">
              Logout
            </button>
          </div>

          <div style={{ textAlign: 'center', marginBottom: '30px' }}>
            <div className="score-display">
              {score}/{totalQuestions}
            </div>
            <div style={{ fontSize: '1.5rem', fontWeight: '600', color: '#333', marginBottom: '10px' }}>
              {percentage}%
            </div>
            <div style={{ fontSize: '1.2rem', color: '#666', marginBottom: '20px' }}>
              {getScoreMessage()}
            </div>
          </div>

          <div style={{ background: '#f8f9fa', padding: '20px', borderRadius: '10px', marginBottom: '30px' }}>
            <h3 style={{ marginBottom: '15px', color: '#333' }}>Quiz Summary:</h3>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '10px', color: '#666' }}>
              <div><strong>Topic:</strong> {quizData.topic}</div>
              <div><strong>Difficulty:</strong> {quizData.difficulty}</div>
              <div><strong>Questions:</strong> {totalQuestions}</div>
              <div><strong>Correct:</strong> {score}</div>
              <div><strong>Incorrect:</strong> {totalQuestions - score}</div>
              <div><strong>Time Used:</strong> {formatTime(300 - timeLeft)}</div>
            </div>
          </div>

          <div style={{ display: 'flex', gap: '10px', justifyContent: 'center' }}>
            <button onClick={restartQuiz} className="btn">
              Take Another Quiz
            </button>
            <button onClick={() => window.location.reload()} className="btn btn-secondary">
              Retake This Quiz
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h1 className="title" style={{ margin: 0, fontSize: '1.8rem' }}>{quizData.topic} Quiz</h1>
          <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
            <div style={{ fontSize: '1.2rem', fontWeight: '600', color: '#667eea' }}>
              {formatTime(timeLeft)}
            </div>
            <button onClick={onLogout} className="btn btn-secondary">
              Logout
            </button>
          </div>
        </div>

        <div className="progress-bar">
          <div 
            className="progress-fill" 
            style={{ width: `${progress}%` }}
          ></div>
        </div>

        <div style={{ textAlign: 'center', marginBottom: '20px', color: '#666' }}>
          Question {currentQuestionIndex + 1} of {totalQuestions}
        </div>

        <div className="question-card">
          <div className="question-text">
            {currentQuestion.question}
          </div>

          <ul className="options-list">
            {currentQuestion.options.map((option, index) => (
              <li
                key={index}
                className={`option-item ${
                  selectedAnswers[currentQuestionIndex] === index ? 'selected' : ''
                }`}
                onClick={() => handleAnswerSelect(index)}
              >
                <span style={{ fontWeight: '600', marginRight: '10px' }}>
                  {String.fromCharCode(65 + index)}.
                </span>
                {option}
              </li>
            ))}
          </ul>
        </div>

        <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '30px' }}>
          <button
            onClick={handlePreviousQuestion}
            className="btn btn-secondary"
            disabled={currentQuestionIndex === 0}
          >
            Previous
          </button>

          <div style={{ display: 'flex', gap: '10px' }}>
            {Object.keys(selectedAnswers).map((index) => (
              <div
                key={index}
                style={{
                  width: '12px',
                  height: '12px',
                  borderRadius: '50%',
                  backgroundColor: parseInt(index) === currentQuestionIndex ? '#667eea' : '#28a745',
                  border: parseInt(index) === currentQuestionIndex ? '2px solid #667eea' : 'none'
                }}
                title={`Question ${parseInt(index) + 1}`}
              />
            ))}
          </div>

          <button
            onClick={handleNextQuestion}
            className="btn"
            disabled={selectedAnswers[currentQuestionIndex] === undefined}
          >
            {currentQuestionIndex === totalQuestions - 1 ? 'Submit Quiz' : 'Next'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default Quiz;
